mapper.select:
SELECT RE.USER_ID, RE.RE_ID, RE.RE_PROFILE, RE.RE_TITLE, RE.RE_INTTI, RE.RE_INTCON, RE.LICENSE, RE.INDATE, UR.USER_NAME, UR.USER_SOCIAL_NUM, UR.USER_PHONE, UR.USER_EMAIL
FROM POSTING PO INNER JOIN COMPANY COM ON PO.COM_ID = COM.COM_ID
WHERE PO.PO_ID = #{ po_id }

SELECT USER_NAME, USER_SOCIAL_NUM, USER_PHONE, USER_EMAIL
FROM USERS
WHERE USER_ID = #{ user_id }

mapper.update:

UPDATE  APPLY
   SET  RESULT = '합격'
 WHERE  AP_ID = #{ ap_id }


UPDATE  APPLY
   SET  RESULT = '불합격'
 WHERE  AP_ID = #{ ap_id }

mapper.delete:
DELETE FROM POSTING
WHERE COM_ID = #{ com_id }
AND PO_ID = #{ po_id }
