mapper.select:
SELECT RE.USER_ID, RE.RE_ID, RE.RE_PROFILE, RE.RE_TITLE, RE.RE_INTTI, RE.RE_INTCON, RE.LICENSE, RE.INDATE, UR.USER_NAME, UR.USER_SOCIAL_NUM, UR.USER_PHONE, UR.USER_EMAIL
FROM RESUME RE INNER JOIN USERS UR ON RE.USER_ID = UR.USER_ID
WHERE RE.RE_ID = #{ re_id }

SELECT USER_NAME, USER_SOCIAL_NUM, USER_PHONE, USER_EMAIL
FROM USERS
WHERE USER_ID = #{ userId }

SELECT RE.USER_ID, RE.RE_ID, RE.RE_PROFILE, RE.RE_TITLE, RE.RE_INTTI, RE.RE_INTCON, RE.LICENSE, RE.INDATE, UR.USER_NAME, UR.USER_SOCIAL_NUM, UR.USER_PHONE, UR.USER_EMAIL
FROM APPLY AP JOIN RESUME RE ON AP.RE_ID = RE.RE_ID JOIN USERS UR ON RE.USER_ID = UR.USER_ID
WHERE AP.AP_ID = #{ ap_id }

SELECT RE.USER_ID, RE.RE_ID, AP.RESULT, AP.AP_ID, PO.PO_ID, PO.COM_ID, PO.PO_TITLE, COM.COM_NAME
FROM RESUME RE JOIN APPLY AP ON RE.RE_ID = AP.RE_ID JOIN POSTING PO ON AP.PO_ID = PO.PO_ID JOIN COMPANY COM ON PO.COM_ID = COM.COM_ID
WHERE RE.USER_ID = #{ userId }
